import React, { Component } from 'react';
import './App.css';
import { render } from '@testing-library/react';
import { CardList } from './components/card-list/card-list.component';

class App extends Component {

  constructor() {
    super();
    this.state = {
      searchField: '',
      monsters: []
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users").then(response => response.json()).then(users => this.setState({monsters: users}))
  }


  render () {
    const { monsters, searchField } = this.state;
    const filteredSearch = this.state.monsters.filter(monster => {monster.name.toLowerCase()}).includes(this.state.searchField.toLowerCase())
    return (
      <div className="App">
              <input type="search" placeholder="Search Monsters" onChange={e => this.setState({searchField: e.target.value}, () => {console.log(this.state)})} />
        <CardList monsters={filteredSearch}></CardList>
      </div>
    );
  }
}

export default App;
